import React, { useState, useRef, useEffect, useCallback } from 'react';

import { useSpring, animated } from 'react-spring';
import styled from 'styled-components';
import { MdClose } from 'react-icons/md';
import Axios from 'axios'

import { NavLink as Link } from 'react-router-dom';

export const Nav = styled.nav`
  background: transparent;
  border-bottom: 2px solid black;
  border-top: 2px solid black;
  height: 34px;
  display: flex;
  padding: 0.5rem calc((62vw - 1000px) / 2);
  margin: 15px 0px;
  z-index: 10;
`;

export const NavLink = styled(Link)`
  margin-left: 30%;
  font: bold 20px Arial;
  text-decoration: none;
  background-color: #EEEEEE;
  color: #333333;
  border-top: 1px solid #CCCCCC;
  border-right: 1px solid #333333;
  border-bottom: 1px solid #333333;
  border-left: 1px solid #CCCCCC;
`;

const OuterBorder = styled.div`
  width: 100%;
  height: 685px;
  position: absolute;
  margin: 13px 0px;
  border: solid 2px;
`;

const CreateBar = styled.div`
  font-family: "Lucida Console", monospace;
  width: 100%;
  height: 6%;
  padding: 6px;
  border-bottom: 2px solid;
  font-size: 22px;
`;

const EditBorder = styled.nav`
  width: 40%;
  height: 590px;
  font-family: 'Arial', sans-serif;
  font-size: 18px;
  overflow: scroll;
`;

const InfoBar = styled.div`
  font-family: "Lucida Console", monospace;
  font-size: 22px;
  align-content: center;
  border-bottom: 2px solid;
  padding-left: 10.5%;
  width: 40%;
`;

const SafetyInfo = styled.div`
    font-family: 'Arial', sans-serif;
    font-size: 19px;
    text-align: center;
    position: absolute;
    right: 30%;
    bottom: 0%;
    height: 41%;
    width: 30%;
    border: 2px solid;
`;

const PhoneInfo = styled.div`
    font-family: 'Arial', sans-serif;
    font-size: 19px;
    text-align: center;
    width: 30%;
    height: 41%;
    position: absolute;
    left: 69.9%;
    bottom: 0%;
    border: 2px solid;
`;

const ReportInfo = styled.div`
    overflow: hidden;
    border: 2px solid;
    font-family: 'Arial', sans-serif;
    font-size: 16px;
    position: absolute;
    padding: 5px;
    text-align: center;
    width: 17%;
    height: 365px;
    right: 43%;
    top: 5.8%;
`;

const Box = styled.div`
    border-bottom: 2px solid;   
    height: 14%;
    margin-bottom: 4px;
`;

const EditButton = styled.button`
  padding-left: 40%;
  padding-right: 40%;
`

const RideSelect = styled.select`
    align: right;
`

const styleGray = {backgroundColor : '#AFAFAF'};
export default function EditPage() {

    //states
const [ride_name, setRide_name] = useState('');
const [dailyOpening, setDailyOpening] = useState('');
const [dailyClosing, setDailyClosing] = useState('');
const [theoryCapacity, setTheoryCapacity] = useState('');
const [targetCapacity, setTargetCapacity] = useState('');
const [minVehicles, setMinVehicles] = useState('');
const [maxVehicles, setMaxVehicles] = useState('');
const [maxSeats, setMaxSeats] = useState('');
const [minStaff, setMinStaff] = useState('');
const [maxStaff, setMaxStaff] = useState('');
const [parkSection, setParkSection] = useState('');
const [weatherCode, setWeatherCode] = useState('');
const [rideType, setRideType] = useState('');

//for the drop down menu
const [rideSelect, setRideSelect] = useState('');

//state to get all attractions
const [attractionList, setAttractionList] = useState([]);
//send the attraction data to the backend running on port 3001
//specifically /addAttraction

//http request
const getAttractions = () => {
  Axios.get('http://localhost:3001/getAttraction').then( (res) => {
    console.log(res); //response
    setAttractionList(res.data);
    return attractionList;
  });
}

const editAttraction = () =>{
  Axios.put('http://localhost:3001/editAttraction', {
                ride_name: ride_name,
                dailyOpening: dailyOpening,
                dailyClosing: dailyClosing,
                theoryCapacity: theoryCapacity,
                targetCapacity: targetCapacity,
                minVehicles: minVehicles,
                maxVehicles: maxVehicles,
                maxSeats: maxSeats,
                minStaff: minStaff,
                maxStaff: maxStaff,
                parkSection: parkSection,
                weatherCode: weatherCode,
                rideType: rideType,

                rideSelect: setRideSelect

                }).then(() =>{
                  alert('successful insert');
              }).then( () => {
                console.log("Successfully sent to port 3001");
              });
};

return (
    <>
    {useEffect(() =>{
      {window.addEventListener('load', getAttractions())}
    })}
    <OuterBorder>
    <CreateBar> Edit an Attraction </CreateBar>


    <RideSelect onChange={(e) => {
                        setRideSelect(e.target.value);
                      }}>
                        <option>Select Attraction</option>
            {attractionList.map((val, key) => {

              return (
                  <>                                
                          <option>{val.ride_name}</option>
                  </>
              );
              })}

          </RideSelect>

    <InfoBar> Basic Information</InfoBar>
    <EditBorder>



    <table className="table table-bordered table-striped">
        <thead>
          <tr style = {styleGray}>

            <th scope="col">Property</th>
            <th scope="col">Value</th>
          </tr>
        </thead>
        <tbody>

          <tr>
          <td>Name</td>
          <td>          

              {attractionList.map((val, key) => {
                    var ride_name;
                    if (rideSelect == val.ride_name){
                        ride_name = val.ride_name;
                        return (
                          <> 
                            <input type='text' name='ride_name' placeHolder={ride_name} onChange={(e) => {
                                setDailyOpening(e.target.value);
                              }}
                            ></input>  
                          </>
                        );
                    }
                })}


          </td>
          </tr>

          <tr>
          <td>Daily Opening</td>
          <th> 


              {attractionList.map((val, key) => {
                var dailyOpening;
                if (rideSelect == val.ride_name){
                    dailyOpening = val.dailyOpening;
                    return (
                      <> 
                        <input type='text' name='dailyOpening' placeHolder={dailyOpening} onChange={(e) => {
                            setDailyOpening(e.target.value);
                          }}
                        ></input>  
                      </>
                    );
                }
                })}

          </th>
          </tr>

          <tr>
          <td>Daily Closing</td>
          <th> 

              {attractionList.map((val, key) => {
                    var dailyClosing;
                    if (rideSelect == val.ride_name){
                        dailyClosing = val.dailyClosing;
                        return (
                          <> 
                            <input type='text' name='dailyClosing' placeHolder={dailyClosing} onChange={(e) => {
                                setDailyOpening(e.target.value);
                              }}
                            ></input>  
                          </>
                        );
                    }
                })}

          </th>
          </tr>

          <tr>
          <td>Theoretical Capacity</td>
          <th> 

              {attractionList.map((val, key) => {
                        var theoryCapacity;
                        if (rideSelect == val.ride_name){
                            theoryCapacity = val.theoryCapacity;
                            return (
                              <> 
                                <input type='text' name='theoryCapacity' placeHolder={theoryCapacity} onChange={(e) => {
                                    setDailyOpening(e.target.value);
                                  }}
                                ></input>  
                              </>
                            );
                        }
              })}

          </th>
          </tr>

          <tr>
          <td>Target Capacity</td>
          <th> 

            {attractionList.map((val, key) => {
                      var targetCapacity;
                      if (rideSelect == val.ride_name){
                          targetCapacity = val.targetCapacity;
                          return (
                            <> 
                              <input type='text' name='targetCapacity' placeHolder={targetCapacity} onChange={(e) => {
                                  setDailyOpening(e.target.value);
                                }}
                              ></input>  
                            </>
                          );
                      }
              })}

          </th>
          </tr>

          <tr>
          <td>Max Vehicles</td>
          <th>

              {attractionList.map((val, key) => {
                          var maxVehicles;
                          if (rideSelect == val.ride_name){
                              maxVehicles = val.maxVehicles;
                              return (
                                <> 
                                  <input type='text' name='maxVehicles' placeHolder={maxVehicles} onChange={(e) => {
                                      setDailyOpening(e.target.value);
                                    }}
                                  ></input>  
                                </>
                              );
                          }
                })}

          </th>
          </tr>

          <tr>
          <td>Min Vehicles</td>
          <th>

              {attractionList.map((val, key) => {
                          var minVehicles;
                          if (rideSelect == val.ride_name){
                              minVehicles = val.minVehicles;
                              return (
                                <> 
                                  <input type='text' name='dailyOpening' placeHolder={minVehicles} onChange={(e) => {
                                      setDailyOpening(e.target.value);
                                    }}
                                  ></input>  
                                </>
                              );
                          }
                })}

          </th>
          </tr>

          <tr>
          <td>Seats per Vehicle</td>
          <th>

              {attractionList.map((val, key) => {
                          var maxSeats;
                          if (rideSelect == val.ride_name){
                              maxSeats = val.maxSeats;
                              return (
                                <> 
                                  <input type='text' name='dailyOpening' placeHolder={maxSeats} onChange={(e) => {
                                      setDailyOpening(e.target.value);
                                    }}
                                  ></input>  
                                </>
                              );
                          }
                })}

          </th>
          </tr>

          <tr>
          <td>Max Employees</td>
          <th>

              {attractionList.map((val, key) => {
                          var maxStaff;
                          if (rideSelect == val.ride_name){
                              maxStaff = val.maxStaff;
                              return (
                                <> 
                                  <input type='text' name='dailyOpening' placeHolder={maxStaff} onChange={(e) => {
                                      setDailyOpening(e.target.value);
                                    }}
                                  ></input>  
                                </>
                              );
                          }
                })}

          </th>
          </tr>

          <tr>
          <td>Min Employees</td>
          <th>

              {attractionList.map((val, key) => {
                          var minStaff;
                          if (rideSelect == val.ride_name){
                              minStaff = val.minStaff;
                              return (
                                <> 
                                  <input type='text' name='dailyOpening' placeHolder={minStaff} onChange={(e) => {
                                      setDailyOpening(e.target.value);
                                    }}
                                  ></input>  
                                </>
                              );
                          }
                })}

          </th>
          </tr>

          <tr>
          <td>Park Section</td>
          <th>

              {attractionList.map((val, key) => {
                          var parkSection;
                          if (rideSelect == val.ride_name){
                              parkSection = val.parkSection;
                              return (
                                <> 
                                  <input type='text' name='dailyOpening' placeHolder={parkSection} onChange={(e) => {
                                      setDailyOpening(e.target.value);
                                    }}
                                  ></input>  
                                </>
                              );
                          }
                })}

          </th>
          </tr>

          <tr>
          <td>Weather Code</td>
          <th>

              {attractionList.map((val, key) => {
                          var weatherCode;
                          if (rideSelect == val.ride_name){
                              weatherCode = val.weatherCode;
                              return (
                                <> 
                                  <input type='text' name='dailyOpening' placeHolder={weatherCode} onChange={(e) => {
                                      setDailyOpening(e.target.value);
                                    }}
                                  ></input>  
                                </>
                              );
                          }
                })}

          </th>
          </tr>

          <tr>
          <td>Type</td>
          <th>

              {attractionList.map((val, key) => {
                          var rideType;
                          if (rideSelect == val.ride_name){
                              rideType = val.rideType;
                              return (
                                <> 
                                  <input type='text' name='dailyOpening' placeHolder={rideType} onChange={(e) => {
                                      setDailyOpening(e.target.value);
                                    }}
                                  ></input>  
                                </>
                              );
                          }
                })}

          </th>
          </tr>

          </tbody>

        <NavLink to='/newAttraction' onClick={editAttraction}>Edit Attraction</NavLink>

        </table>

    </EditBorder>
    <ReportInfo>
        <Box> 
        Required <ul>  Documentation</ul> </Box>
        <ul>Operations Report</ul>  
        <ul>Turnstile Report</ul> 
        <ul>Lockout Report</ul>
    </ReportInfo>
    <SafetyInfo>Safety Critical Information</SafetyInfo>
    <PhoneInfo>Phone Information</PhoneInfo>
    </OuterBorder>
    </>
    );
}
