import styled from 'styled-components';
import plus from './plusSign.png'
import { AttractionModal } from './AttractionModal';
import { GlobalStyle } from '../../globalStyles';
import React, { useState, useEffect } from 'react';
import Axios from 'axios'


const CallsBorder = styled.div`
overflow: hidden;
background: transparent;
margin: 15px 0px;
position: absolute;
left: 25%;
width: 20%;
height: 240px;
border-right: 2px solid black;
`
const RideName = styled.div`
text-align: center;
font-size: 30px;
`

const PauseButton = styled.button`
margin-top: 2px;
margin-left: 20px;
justify-content: center;
height: 17%;
width: 85%;
border: 2px solid black;
background: transparent;
`
const MitButton = styled.button`
margin-top: 50px;
margin-left: 20px;
justify-content: center;
height: 17%;
width: 85%;
border: 2px solid black;
background: transparent;
`
const EndButton = styled.button`
margin-top: 2px;
margin-left: 20px;
justify-content: center;
height: 17%;
width: 85%;
border: 2px solid black;
background: transparent;
`
const AddIntervalsBorder = styled.tr`
overflow: hidden;
background: transparent;
margin: 15px 0px;
position: absolute;
left: 45%;
width: 43.8%;
height: 240px;
overflow: hidden;
overflow-x: scroll;
white-space: nowrap;
`;


const IntervalCard = styled.div`
width: 50%;
height: 100%;
border-right: 2px solid black;
display: inline-block;
`
const CardTime = styled.div`
position: absolute;
top: 0%;
height: 16%;
width: 50%;
border-bottom: 2px solid black;
text-align: center;
border-bottom: 2px solid black;
font-size: 90%;
font-weight: bold;
`
const CardCollect = styled.div`
position: absolute;
top: 16%;
height: 28%;
width: 50%;
border-bottom: 2px solid black;
text-align: left;
font-size: 90%;
font-weight: bold;
`


const CardStarting = styled.div`
position: absolute;
top: 44%;
height: 28%;
width: 50%;
border-bottom: 2px solid black;
text-align: left;
font-size: 90%;
font-weight: bold;
`
const CardEnding = styled.div`
position: absolute;
top: 72%;
height: 28%;
width: 20%;
text-align: left;
font-size: 90%;
font-weight: bold;
`
const CardH = styled.h1`
position: absolute;
left: 0%;
top: 0%;
margin-top: 3px;
margin-left: 3px;
`

const Image = styled.img`
display:flex;
height: 70px;
border: none;
`
const Button = styled.button`
display: inline-block;
background: transparent;
position: absolute;
left: 17.5%;
top: 35%;
border: none;
`
const RideSelect = styled.select`
    align: right;
`
export var rideName;
const AttractionIntervals = () => {
    const [showModal, setShowModal] = useState(false);

    const openModal = () => {
    setShowModal(prev => !prev);
    };


        //states
        const [startingTime, setStartingTime] = useState('');
        const [endingTime, setEndingTime] = useState('');
        const [timeValue, setTime] = useState('');
        const [typeState, setTypeState] = useState([]);
        const [unitState, setUnitState] = useState("");
        const [intervalList, setIntervalList] = useState([]);
        const [rideSelect, setRideSelect] = useState('');

          //state to get all attractions
    const [attractionList, setAttractionList] = useState([]);
         //recieve data from backend to display
         const GetAttractions = () => {
            //console.log(res.data)
            useEffect(() => {
                Axios.get('http://localhost:3001/getAttraction').then(res => {
                setAttractionList(res.data);
                }).catch(err => console.log(err));
                }, [])
        }

        const GetIntervals = () => {

            Axios.get(`http://localhost:3001/getInterval/:${rideSelect}`).then(res => {
            console.log(rideSelect)
            setIntervalList(res.data)
            }).catch(err => console.log(err));
        }

        const submitShowRideIntervals = () => {

            GetIntervals();
            setIntervalList(rideName);

        }

    return (
        <>

            <CallsBorder>
                <RideName>  
                {window.addEventListener('load', GetAttractions())}
                    <RideSelect onChange={(e) => {

                        setRideSelect(e.target.value);
                        rideName = e.target.value;
                      }}
                      >
                        <option>Select Attraction</option>


            {attractionList.map((val, key) => {

              return (
                  <>                                
                          <option>{val.ride_name}</option>
                  </>
              );
              })}
        </RideSelect>

                </RideName>
                <MitButton onClick={submitShowRideIntervals}>Show Rides Intervals</MitButton>
                <PauseButton> Pause Calls </PauseButton>
                <EndButton> End Todays Calls </EndButton>
            </CallsBorder>

            <AddIntervalsBorder>

            <IntervalCard>

                <Button onClick={openModal}> <Image src={plus} Image/> </Button>
                <AttractionModal showModal={showModal} setShowModal={setShowModal} />
                    <GlobalStyle /> 

            </IntervalCard> 

            {useEffect(() => {
            Axios.get(`http://localhost:3001/getInterval/:${rideSelect}`).then(res => {
            console.log(rideSelect)
            setIntervalList(res.data)
            }).catch(err => console.log(err));
            }, [])}
                {intervalList.map((val, key) => {
                        return (
                            <>
                            <IntervalCard>
                            <CardTime>Every {val.timeValue} Minutes</CardTime>
                            <CardCollect>Collect
                                 <li> 
                                     {val.typeState}

                                 </li>
                                </CardCollect>
                            <CardStarting>Starting<ul>At Park Opening</ul> <ul>{val.startingTime}</ul></CardStarting>
                            <CardEnding>Ending<ul>At Park Closing</ul><ul>{val.endingTime}</ul></CardEnding>
                            </IntervalCard>
                            </>
                        );
                        })}

            </AddIntervalsBorder>

        </>
    )
}

export default AttractionIntervals
