/* eslint-disable */
import React, { useState, useEffect } from "react";
import styled from 'styled-components';
import Axios from 'axios'
import { saveLists } from './ChartLine';


const SubmitButton = styled.button`
  height: 40px;
  width: 100%;
  border-radius: 9px;
  font-size: 20px;
`


function GraphCheck() {
  var [rides, setRides] = useState([]);
  var [stats, setStats] = useState([]);
  const CheckedRideName = () => {

    Axios.get(`http://localhost:3001/sendRideNameGraph`).then(res => {
        //console.log(res.data)
        setRides(res.data)
    }).catch(err => console.log(err));
  }

  const CheckedStat = () => {
    Axios.get(`http://localhost:3001/sendStatsGraph`).then(res => {
        //console.log(res.data)
        setStats(res.data)
    }).catch(err => console.log(err));
}
  const sendStats = (statList) =>{
    Axios.post('http://localhost:3001/sendStatsBackend', {
      statList: statList
                  }).then(() =>{
                    alert('successful insert');
                }).then( () => {
                  console.log("Successfully sent to port 3001");
                });
  };

  const sendRideName = (rideList) =>{
    Axios.post('http://localhost:3001/sendRideNameBackend', {
      rideList: rideList
                  }).then(() =>{
                    alert('successful insert');
                }).then( () => {
                  console.log("Successfully sent to port 3001");
                });
  };

  const GetAttractions = () => {
    //console.log(res.data)
    useEffect(() => {
        Axios.get('http://localhost:3001/getAttraction').then(res => {
        setAttractionList(res.data);
        }).catch(err => console.log(err));
        }, [])
}

  var [statState, setStatState] = useState([]);
  var [statList, setStatList] = useState([]);
  const styleGray = {backgroundColor : '#AFAFAF'};
  var [ride_name, setRide_name] = useState('');
  var [rideList, setRideList] = useState([]);
  const [attractionList, setAttractionList] = useState([]);

  var checkStats = []
  const pushStats = () => {
    statState.map(data => {
    checkStats.push({statistic: data.statistic, state: data.select})
    })
  }

  var checked
  useEffect(() => {

    let statState = [
      { id: 1, statistic: "Throughput", select: stats.includes("Throughput")},
      { id: 2, statistic: "Wait Time", select: stats.includes("Wait Time")},
      { id: 3, statistic: "Available Seats", select: stats.includes("Available Seats")},
      { id: 4, statistic: "Available Down", select: stats.includes("Available Down")},
    ];
    setStatState(
      statState.map(d => {

        return {
          select: d.select,
          id: d.id,
          statistic: d.statistic,

        };
      })
    );
  }, []);

  return (

    <div>
      {pushStats(), console.log(checkStats)}
        {window.addEventListener('load', GetAttractions())}
        {console.log(rides, stats)}

        {useEffect(() => {
            {window.addEventListener('load', CheckedRideName())}
            {window.addEventListener('load', CheckedStat())}

        }, [])}
        <table className="table table-bordered table-striped">
        <thead>
          <tr style={styleGray}>
            <th scope="col">Ride Name</th>
            <th scope="col">Include</th>
          </tr>
        </thead>
        <tbody>
                {attractionList.map((val, key) => {
                  return (
                    <>  

                        <tr>
                          <td scope="row">{val.ride_name}</td> <td>
                            <input type="checkbox" onClick={() => {
                            console.log(val.ride_name)
                            setRide_name(val.ride_name)
                            if (!rideList.includes(val.ride_name)){
                              rideList = rideList.push(val.ride_name)
                            }
                          }}></input> </td>
                        </tr>
                    </>
                  );
                  })}
        </tbody>
      </table>
      <table className="table table-bordered table-striped">
        <thead>
          <tr style={styleGray}>
            <th scope="col">Statistic</th>
            <th scope="col">Include</th>
          </tr>
        </thead>
        <tbody>
          {statState.map((d, i) => (
            <tr key={d.id}>
              <td>{d.statistic}</td>
              <th scope="row">

             <input

              onChange={event => {
                checked = event.target.checked;
                checkStats[d.id] = checked
                  if(statList.includes(d.statistic))
                  {
                    statList = statList.filter(e => e !== d.statistic);
                    console.log('removing from list')
                  }
                  else{
                    statList.push(d.statistic)
                    console.log('adding to list')
                  }
              }}
              type="checkbox"
              checked={checked}
              ></input>

              </th>
            </tr>
          ))}
        </tbody>
      </table>
      <SubmitButton onClick={() => {
        localStorage.clear();
        // console.log(statList);
        // console.log(rideList)
        sendRideName(rideList);
        sendStats(statList);
        {console.log('stat',checkStats)}
        setTimeout(function(){
          window.location.reload(); 
         }, 2);
      }}>Submit</SubmitButton>
    </div>
  );
}

export default GraphCheck;
