text-align: center;
padding: 12%;
font-size: 20px;
font-weight: bold;
margin: 0px 0px;
`;

export const QuickToolsButtons = styled.button`
width: 100%;
height: 88%;
padding: 1px;
font-size: 16px;
border-bottom: 1px solid black;
`;

const QuickToolsPanel = () => {
const [showModal, setShowModal] = useState(false);

const openModal = () => {
setShowModal(prev => !prev);
};
