        <>
            <QuickToolsSideNav>
                <QuickToolsHeader>
                    Quick Tools
                </QuickToolsHeader>
                    <NavLink to = '/CreatePage'>
                    <QuickToolsButtons>New Attraction</QuickToolsButtons>
                    </NavLink>



            </QuickToolsSideNav>
        </>
    );
}

export default QuickToolsPanel
