import React, { useState,useEffect } from 'react';
import styled from 'styled-components';
import Axios from 'axios'
import { NavLink as Link } from 'react-router-dom';
import Navbar from '../components/General/Navbar';
import Banner from '../components/General/Bannerbar';

export const Nav = styled.nav`
  background: transparent;
  height: 3px;
  display: flex;
  position: absolute;
  top: 3%;
  right: 7%;
  font-size: 15px;
`;
export const NavLink = styled(Link)`
  color: black;
  border-right: 10px solid transparent;
  border-bottom: 25px solid lightgray;
  display: block;
  margin: 0 -0.3%;
  align-items: center;
  text-decoration: none;
  padding: 2px;
  height: 100%;
  cursor: default;
  &.active {
    color: black;
  }
`;

export const NavMenu = styled.div`
  display: flex;
  align-items: center;
`;


export const DeleteLink = styled(Link)`
  margin-left: 40%;
  font: bold 20px Arial;
  text-decoration: none;
  background-color: #EEEEEE;
  color: #333333;
  border-top: 1px solid #CCCCCC;
  border-right: 1px solid #333333;
  border-bottom: 1px solid #333333;
  border-left: 1px solid #CCCCCC;
`;

const Upload = styled.div`
    border: 0px solid;   
    height: 54%;
    width: 43%;
    position: absolute;
    top: 6%;
    right: 0%;
`;

const OuterBorder = styled.div`
  width: 100%;
  height: 685px;
  position: absolute;
  margin: 13px 0px;
  border: solid 2px;
`;

const DeleteBar = styled.div`
  font-family: "Lucida Console", monospace;
  width: 100%;
  height: 6%;
  padding: 6px;
  border-bottom: 2px solid;
  font-size: 22px;
`;

const EditBorder = styled.nav`
  width: 40%;
  height: 607px;
  border-right: 2px solid;
  font-family: 'Arial', sans-serif;
  font-size: 18px;
  overflow: scroll;
`;

const InfoBar = styled.div`
  font-family: "Lucida Console", monospace;
  font-size: 22px;
  align-content: center;
  border-bottom: 2px solid;
  padding-left: 5%;
  text-align: center;
  border-right: 2px solid;

`;

const SafetyInfo = styled.div`
    font-family: 'Arial', sans-serif;
    font-size: 19px;
    text-align: center;
    position: absolute;
    right: 30%;
    bottom: 0%;
    height: 41%;
    width: 30%;
    border: 2px solid;
    overflow: scroll;
`;

const PhoneInfo = styled.div`
    font-family: 'Arial', sans-serif;
    font-size: 19px;
    text-align: center;
    width: 30%;
    height: 41%;
    position: absolute;
    left: 69.9%;
    bottom: 0%;
    border: 2px solid;
`;

const ReportInfo = styled.div`
    overflow: hidden;
    border: 2px solid;
    font-family: 'Arial', sans-serif;
    font-size: 16px;
    position: absolute;
    padding: 5px;
    text-align: center;
    width: 17%;
    height: 365px;
    right: 43%;
    top: 5.8%;
`;

const Box = styled.div`
    border-bottom: 2px solid;   
    height: 14%;
    margin-bottom: 4px;
`;

const DeleteButton = styled.button`
  padding-left: 40%;
  padding-right: 40%;
  margin-top: -500px;
  width: 100%;
  height: 8%;
`





const styleGray = {backgroundColor : '#AFAFAF'};
export default function DeletePage(props) {



  //states
  const [ride_name, setRideName] = useState('');


const [rideSelect, setRideSelect] = useState('');

//state to get all attractions
const [attractionList, setAttractionList] = useState([]);
//send the attraction data to the backend running on port 3001
//specifically /addAttraction

//recieve data from backend to display
const getAttractions = () => {
  Axios.get('http://18.204.6.173:3001/getAttraction').then( (res) => {
      //console.log(res.data)
      return setAttractionList(res.data);
  });

}


const deleteAttraction = (rideSelect) => {
  Axios.delete(`http://18.204.6.173:3001/deleteAttraction/${rideSelect}`);
};

return (
    <>
    <Navbar/>
    <Banner/>
    <OuterBorder>
    <DeleteBar> Delete an Attraction 
    <Nav>
         <NavMenu>
             <NavLink to='/DeletePage/Block' activeStyle>
                 Block Section
             </NavLink>
             <NavLink to='/DeletePage/Danger' activeStyle>
                 Danger Areas
             </NavLink>
             <NavLink to='/DeletePage/Restrict' activeStyle>
                 Restricted Areas
             </NavLink>
             </NavMenu>
    </Nav>
    </DeleteBar>

    <EditBorder>
    <InfoBar> Basic Information</InfoBar>
        {useEffect(() =>{
              {window.addEventListener('load', getAttractions())}
              {window.addEventListener('load', setRideSelect(props.location.ride_name))}
            })}
        <table className="table table-bordered table-striped">
        <thead>
          <tr style = {styleGray}>
            <th scope="col">Property</th>
            <th scope="col">Value</th>
          </tr>
        </thead>
        <tbody>

<tr>
<td>Name</td>
<td>          


                <> 
                <label> {props.location.ride_name} </label>
                </>




</td>
</tr>

<tr>
<td>Daily Opening</td>
<th> 


    {attractionList.map((val, key) => {
      var dailyOpening;
      if (rideSelect == val.ride_name){
          dailyOpening = val.dailyOpening;
          return (
            <> 
              <label> {dailyOpening} </label>
            </>
          );
      }
      })}

</th>
</tr>

<tr>
<td>Daily Closing</td>
<th> 

    {attractionList.map((val, key) => {
          var dailyClosing;
          if (rideSelect == val.ride_name){
              dailyClosing = val.dailyClosing;
              return (
                <> 
                  <label> {dailyClosing} </label>
                </>
              );
          }
      })}

</th>
</tr>

<tr>
<td>Theoretical Capacity</td>
<th> 

    {attractionList.map((val, key) => {
              var theoryCapacity;
              if (rideSelect == val.ride_name){
                  theoryCapacity = val.theoryCapacity;
                  return (
                    <> 
                      <label> {theoryCapacity} </label>
                    </>
                  );
              }
    })}

</th>
</tr>

<tr>
<td>Target Capacity</td>
<th> 

  {attractionList.map((val, key) => {
            var targetCapacity;
            if (rideSelect == val.ride_name){
                targetCapacity = val.targetCapacity;
                return (
                  <> 
                    <label> {targetCapacity} </label>
                  </>
                );
            }
    })}

</th>
</tr>

<tr>
<td>Max Vehicles</td>
<th>

    {attractionList.map((val, key) => {
                var maxVehicles;
                if (rideSelect == val.ride_name){
                    maxVehicles = val.maxVehicles;
                    return (
                      <> 
                        <label> {maxVehicles} </label>
                      </>
                    );
                }
      })}

</th>
</tr>

<tr>
<td>Min Vehicles</td>
<th>

    {attractionList.map((val, key) => {
                var minVehicles;
                if (rideSelect == val.ride_name){
                    minVehicles = val.minVehicles;
                    return (
                      <> 
                        <label> {minVehicles} </label>
                      </>
                    );
                }
      })}

</th>
</tr>

<tr>
<td>Seats per Vehicle</td>
<th>

    {attractionList.map((val, key) => {
                var maxSeats;
                if (rideSelect == val.ride_name){
                    maxSeats = val.maxSeats;
                    return (
                      <> 
                        <label> {maxSeats} </label>
                      </>
                    );
                }
      })}

</th>
</tr>

<tr>
<td>Max Employees</td>
<th>

    {attractionList.map((val, key) => {
                var maxStaff;
                if (rideSelect == val.ride_name){
                    maxStaff = val.maxStaff;
                    return (
                      <> 
                        <label> {maxStaff} </label>
                      </>
                    );
                }
      })}

</th>
</tr>

<tr>
<td>Min Employees</td>
<th>

    {attractionList.map((val, key) => {
                var minStaff;
                if (rideSelect == val.ride_name){
                    minStaff = val.minStaff;
                    return (
                      <> 
                        <label> {minStaff} </label>
                      </>
                    );
                }
      })}

</th>
</tr>

<tr>
<td>Park Section</td>
<th>

    {attractionList.map((val, key) => {
                var parkSection;
                if (rideSelect == val.ride_name){
                    parkSection = val.parkSection;
                    return (
                      <> 
                        <label> {parkSection} </label>  
                      </>
                    );
                }
      })}

</th>
</tr>

<tr>
<td>Weather Code</td>
<th>

    {attractionList.map((val, key) => {
                var weatherCode;
                if (rideSelect == val.ride_name){
                    weatherCode = val.weatherCode;
                    return (
                      <> 
                        <label> {weatherCode} </label>
                      </>
                    );
                }
      })}

</th>
</tr>

<tr>
<td>Type</td>
<th>

    {attractionList.map((val, key) => {
                var rideType;
                if (rideSelect == val.ride_name){
                    rideType = val.rideType;
                    return (
                      <> 
                        <label> {rideType} </label> 
                      </>
                    );
                }
      })}

</th>
</tr>

</tbody>



        </table>

        <DeleteButton onClick={() => {
            deleteAttraction(props.location.ride_name) //ride selected in drop down menu
            window.alert(`The ride: ${props.location.ride_name} has been deleted`);
            setTimeout(function(){
              window.location.href='/newAttraction';
            }, 10);
          }}>Delete Attraction</DeleteButton>

    </EditBorder>
    <ReportInfo>
        <Box> 
        Required <ul>  Documentation</ul> </Box>
        <ul>Operations Report</ul>  
        <ul>Turnstile Report</ul> 
        <ul>Lockout Report</ul>
    </ReportInfo>

    <Upload>  
    <h4 className='display-4 text-center mb-4'>
      <i className='fab fa-react' /> Titan Upload</h4> </Upload>

    <SafetyInfo>
      <InfoBar>Safety Critical Information</InfoBar>
      <table className="table table-bordered table-striped">
        <thead>
          <tr style = {styleGray}>
            <th scope="col">Property</th>
            <th scope="col">Value</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>Weather Code</td>
            <td>Alpha</td>
          </tr>

          <tr>
            <td>Max Operating Wind</td>
            <td>34 MPH</td>
          </tr>

          <tr>
            <td>Min Operating Temperature</td>
            <td>41 F</td>
          </tr>

          <tr>
            <td>Power Redundancy</td>
            <td>On-Site</td>
          </tr>

          <tr>
            <td>Number of Gates</td>
            <td>11</td>
          </tr>
        </tbody>
      </table>
    </SafetyInfo>
    <PhoneInfo>
      <InfoBar>Phone Information</InfoBar>
      <table className="table table-bordered table-striped">
        <thead>
          <tr style = {styleGray}>
            <th scope="col">Name</th>
            <th scope="col">Number</th>
            <th scope="col">Out</th>
            <th scope="col">In</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>Primary</td>
            <td>3152</td>
            <td>
              <input type='checkbox'></input>
            </td>
            <td>
              <input type='checkbox'></input>
            </td>
          </tr>

          <tr>
            <td>Secondary</td>
            <td>3153</td>
            <td>
              <input type='checkbox'></input>
            </td>
            <td>
              <input type='checkbox'></input>
            </td>
          </tr>

          <tr>
            <td>Tertiary</td>
            <td>3154</td>
            <td>
              <input type='checkbox'></input>
            </td>
            <td>
              <input type='checkbox'></input>
            </td>
          </tr>
        </tbody>
      </table>
    </PhoneInfo>
    </OuterBorder>
    </>
    );
}
