import React, { useState, useRef, useEffect, useCallback } from 'react';

import { useSpring, animated } from 'react-spring';
import styled from 'styled-components';
import { MdClose } from 'react-icons/md';
import Axios from 'axios'
import { NavLink as Link } from 'react-router-dom';

export const Nav = styled.nav`
  background: transparent;
  height: 3px;
  display: flex;
  position: absolute;
  top: 3%;
  right: 7%;
  font-size: 15px;
`;

export const NavLink = styled(Link)`
  color: black;
  border-bottom: 25px solid lightgray;
  border-right: 10px solid transparent;
  display: block;
  margin: 0 -0.3%;
  align-items: center;
  text-decoration: none;
  padding: 2px;
  height: 100%;
  cursor: default;
  &.active {
    color: black;
  }
`;

export const NavMenu = styled.div`
  display: flex;
  align-items: center;
`;

const OuterBorder = styled.div`
  width: 100%;
  height: 660px;
  position: absolute;
  margin: 13px 0px;
  border: solid 2px;
`;

const DeleteBar = styled.div`
  font-family: "Lucida Console", monospace;
  width: 100%;
  height: 6%;
  padding: 6px;
  border-bottom: 2px solid;
  font-size: 22px;
`;

const EditBorder = styled.nav`
  width: 40%;
  height: 615px;
  border-right: 2px solid;
  font-family: 'Arial', sans-serif;
  font-size: 18px;
  overflow: scroll;
`;

const InfoBar = styled.div`
  font-family: "Lucida Console", monospace;
  font-size: 22px;
  align-content: center;
  text-align: center;
  border-bottom: 2px solid;
  border-right: 2px solid;
`;

const SafetyInfo = styled.div`
    font-family: 'Arial', sans-serif;
    font-size: 18px;
    position: absolute;
    right: 30%;
    bottom: 0%;
    height: 40%;
    width: 30%;
    border: 2px solid;
    overflow: scroll;
`;

const PhoneInfo = styled.div`
    font-family: 'Arial', sans-serif;
    font-size: 18px;
    width: 30%;
    height: 40%;
    position: absolute;
    left: 69.9%;
    bottom: 0;
    border: 2px solid;
    overflow: scroll;
`;

const ReportInfo = styled.div`
    overflow: hidden;
    border: 2px solid;
    font-family: 'Arial', sans-serif;
    font-size: 16px;
    position: absolute;
    padding: 5px;
    text-align: center;
    width: 17%;
    height: 357px;
    right: 43%;
    top: 5.8%;
`;

const Box = styled.div`
    border-bottom: 2px solid;   
    height: 14%;
    margin-bottom: 4px;
`;

const DeleteButton = styled.button`
    padding-right: 40%;
    padding-left: 40%;
`
const Upload = styled.div`
    border: 0px solid;   
    height: 54%;
    width: 43%;
    position: absolute;
    top: 6%;
    right: 0%;
`;


const styleGray = {backgroundColor : '#AFAFAF'};
export default function DeletePage() {



  //states
const [ride_name, setRide_name] = useState('');
const [rideModel, setRideModel] = useState('');
const [rideArea, setRideArea] = useState('');
const [theoryCapacity, setTheoryCapacity] = useState('');
const [targetCapacity, setTargetCapacity] = useState('');
const [maxVehicles, setMaxVehicles] = useState('');
const [minVehicles, setMinVehicles] = useState('');
const [maxSeats, setMaxSeats] = useState('');
const [minStaff, setMinStaff] = useState('');
const [maxStaff, setMaxStaff] = useState('');
const [powerReq, setPowerReq] = useState('');
const [maxHeight, setMaxHeight] = useState('');
const [trackLength, setTrackLength] = useState('');

const [rideSelect, setRideSelect] = useState('');

//state to get all attractions
const [attractionList, setAttractionList] = useState([]);
//send the attraction data to the backend running on port 3001
//specifically /addAttraction

const getAttractions = () => {
  Axios.get('http://localhost:3001/getAttractionNames').then( (res) => {

    console.log(res); //response
    setAttractionList(res.data);
    console.log(res.data)
  });
}


const deleteAttraction = (ride_name) => {
  Axios.delete(`http://localhost:3001/deleteAttraction/${ride_name}`);
};

return (
    <>

    <OuterBorder>
    <DeleteBar> Delete an Attraction 

    <Nav>
         <NavMenu>
             <NavLink to='/DeletePage/Block' activeStyle>
                 Block Section
             </NavLink>
             <NavLink to='/DeletePage/Danger' activeStyle>
                 Danger Areas
             </NavLink>
             <NavLink to='/DeletePage/Restrict' activeStyle>
                 Restricted Areas
             </NavLink>
             </NavMenu>
    </Nav> 
    </DeleteBar>
    <EditBorder>
      <InfoBar> Basic Information</InfoBar>

    {/* <RideSelect onSelect={(e) => {
                        setRideSelect(e.target.value);
                      }}>
                        {attractionList.map((val, key) => {
                          return (
                              <>                                
                                      <option>{val.ride_name}</option>
                              </>
                          );
                        })}
        </RideSelect> */}


            {useEffect(() =>{
              {window.addEventListener('load', getAttractions())}
            })}
        <table className="table table-bordered table-striped">
        <thead>
          <tr style = {styleGray}>
            <th scope="col">Property</th>
            <th scope="col">Value</th>
          </tr>
        </thead>
        <tbody>


        <tr>
        <td>Name</td>
        <td>                 

        <input type='text' name='ride_name' onChange={(e) => {
                    setRide_name(e.target.value);
                  }}></input> 

        </td>
        </tr>

        <tr>
        <td>Model</td>
        <th> 

        </th>
        </tr>

        <tr>
        <td>Area</td>
        <th> 

        </th>
        </tr>

        <tr>
        <td>Theoretical Capacity</td>
        <th> 

        </th>
        </tr>

        <tr>
        <td>Target Capacity</td>
        <th> 

        </th>
        </tr>

        <tr>
        <td>Max Vehicles</td>
        <th>

        </th>
        </tr>

        <tr>
        <td>Min Vehicles</td>
        <th>

        </th>
        </tr>

        <tr>
        <td>Seats per Vehicle</td>
        <th>

        </th>
        </tr>

        <tr>
        <td>Max Employees</td>
        <th>

        </th>
        </tr>

        <tr>
        <td>Min Employees</td>
        <th>

        </th>
        </tr>

        <tr>
        <td>Power Requirement</td>
        <th>

        </th>
        </tr>

        <tr>
        <td>Max Height</td>
        <th>

        </th>
        </tr>

        <tr>
        <td>Track Length</td>
        <th>

        </th>
        </tr>

        </tbody>

        <DeleteButton onClick={() => {
          window.alert(`The ride: ${ride_name} has been deleted`);
          deleteAttraction(ride_name)
        }}>Delete Attraction</DeleteButton>
        </table>

    </EditBorder>
    <ReportInfo>
        <Box> 
        Required <ul>  Documentation</ul> </Box>
        <ul>Operations Report</ul>  
        <ul>Turnstile Report</ul> 
        <ul>Lockout Report</ul>
    </ReportInfo>

    <Upload>  
    <h4 className='display-4 text-center mb-4'>
      <i className='fab fa-react' /> Titan Upload</h4> </Upload>

    <SafetyInfo>
      <InfoBar>Safety Critical Information</InfoBar>
      <table className="table table-bordered table-striped">
        <thead>
          <tr style = {styleGray}>
            <th scope="col">Property</th>
            <th scope="col">Value</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>Weather Code</td>
            <td>Alpha</td>
          </tr>

          <tr>
            <td>Max Operating Wind</td>
            <td>34 MPH</td>
          </tr>

          <tr>
            <td>Min Operating Temperature</td>
            <td>41 F</td>
          </tr>

          <tr>
            <td>Power Redundancy</td>
            <td>On-Site</td>
          </tr>

          <tr>
            <td>Number of Gates</td>
            <td>11</td>
          </tr>
        </tbody>
      </table>
    </SafetyInfo>
    <PhoneInfo>
      <InfoBar>Phone Information</InfoBar>
      <table className="table table-bordered table-striped">
        <thead>
          <tr style = {styleGray}>
            <th scope="col">Name</th>
            <th scope="col">Number</th>
            <th scope="col">Out</th>
            <th scope="col">In</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>Primary</td>
            <td>3152</td>
            <td>
              <input type='checkbox'></input>
            </td>
            <td>
              <input type='checkbox'></input>
            </td>
          </tr>

          <tr>
            <td>Secondary</td>
            <td>3153</td>
            <td>
              <input type='checkbox'></input>
            </td>
            <td>
              <input type='checkbox'></input>
            </td>
          </tr>

          <tr>
            <td>Tertiary</td>
            <td>3154</td>
            <td>
              <input type='checkbox'></input>
            </td>
            <td>
              <input type='checkbox'></input>
            </td>
          </tr>
        </tbody>
      </table>
    </PhoneInfo>
    </OuterBorder>
    </>
    );
}
