import React, { useState, useEffect } from 'react';
import './App.css';
import Navbar from './components/General/Navbar';
import { BrowserRouter as Router, Switch, Route, Link, NavLink } from 'react-router-dom';
import Main from './pages/main';
import Historical from './pages/historical';
import Graph from './pages/graph';
import Andon from './pages/andon';
import Weather from './pages/weather';
import Attraction from './pages/attraction';
import Banner from './components/General/Bannerbar';
import newAttraction from './pages/newAttraction';
import Edit from './pages/editpage';
import Create from './pages/createpage';
import Delete from './pages/deletepage';
import Interval from './pages/interval';
import RideInfo from './pages/rideInfo';


const App = () => {
  const[user, setUser] = useState('');
  const[email, setEmail] = useState('');
  const[password, setPassword] = useState('');
  const[emailError, setEmailError] = useState('');
  const[passwordError, setPasswordError] = useState('');

  const clearInputs = () => {
    setEmail('');
    setPassword('');
  }

  const clearErrors = () => {
    setEmailError('');
    setPasswordError('');
  }

  const handleLogin = () => {
    clearErrors();
    fire
    .auth()
    .signInWithEmailAndPassword(email, password)
    .catch(err => {
      switch(err.code){
          case "auth/invalid-email":
          case "auth/user-disabled":
          case "auth/user-not-found":
            setEmailError(err.message);
            break;
          case "auth/wrong-password":
            setPasswordError(err.message);
            break;
      }
    })
  };

  const handleLogout = () => {
    fire.auth().signOut();
  };

  const authListener = () => {
    fire.auth().onAuthStateChanged(user => {
      if (user) {
        clearInputs();
        setUser(user);
      } else {
        setUser("");
      }

    });
  }

  useEffect(() => {
    authListener();
  }, []);

  return (
    <>
    <Login email={email} setEmail={setEmail} password={password} setPassword={setPassword} handleLogin={handleLogin} emailError={emailError} passwordError={passwordError}/>
    <Router>
    <Navbar />
    <Banner />
      <Switch>
        <Route path='/' exact component={Main} />
        <Route path='/main' component={Main} />
        <Route path='/historical' component={Historical} />
        <Route path='/graph' component={Graph} />
        <Route path='/andon' component={Andon} />
        <Route path='/weather' component={Weather} />
        <Route path='/attraction' component={Attraction} />
        <Route path='/newAttraction' component={newAttraction} />
        <Route path='/editpage' component={Edit} />
        <Route path='/createpage' component={Create} />
        <Route path='/deletepage' component={Delete} />
        <Route path='/interval' component={Interval} />
        <Route path='/rideInfo' component={RideInfo} />
      </Switch>

    </Router>
    </>
  );
}

export default App;
