import React, { useState, useRef, useEffect, useCallback } from 'react';
import FileUpload from '../components/FileUpload';
import { useSpring, animated } from 'react-spring';
import styled from 'styled-components';
import { MdClose } from 'react-icons/md';
import Axios from 'axios'

const OuterBorder = styled.div`
  width: 100%;
  height: 660px;
  position: absolute;
  margin: 13px 0px;
  border: solid 2px;
`;

const EditBar = styled.div`
  font-family: "Lucida Console", monospace;
  width: 100%;
  height: 6%;
  padding: 6px;
  border-bottom: 2px solid;
  font-size: 22px;
`;

const EditBorder = styled.nav`
  width: 40%;
  height: 600px;
  font-family: 'Arial', sans-serif;
  font-size: 18px;
  overflow: scroll;
`;

const InfoBar = styled.div`
  font-family: "Lucida Console", monospace;
  font-size: 22px;
  align-content: center;
  text-align: center;
  border-bottom: 2px solid;
  border-right: 2px solid;
`;

const SafetyInfo = styled.div`
    font-family: 'Arial', sans-serif;
    font-size: 18px;
    position: absolute;
    right: 30%;
    bottom: 0%;
    height: 40%;
    width: 30%;
    border: 2px solid;
    overflow: scroll;
`;

const PhoneInfo = styled.div`
    font-family: 'Arial', sans-serif;
    font-size: 18px;
    width: 30%;
    height: 40%;
    position: absolute;
    left: 69.9%;
    bottom: 0%;
    border: 2px solid;
    overflow: scroll;
`;

const ReportInfo = styled.div`
    overflow: hidden;
    border: 2px solid;
    font-family: 'Arial', sans-serif;
    font-size: 16px;
    position: absolute;
    padding: 5px;
    text-align: center;
    width: 17%;
    height: 357px;
    right: 43%;
    top: 5.8%;
`;

const Box = styled.div`
    border-bottom: 2px solid;   
    height: 14%;
    margin-bottom: 4px;
`;
const Upload = styled.div`
    border: 1px solid ;   
    height: 54%;
    width: 43%;
    position: absolute;
    top: 6%;
    right: 0%;
`;
const EditButton = styled.button`
  padding-left: 40%;
  padding-right: 40%;
`

const RideSelect = styled.select`
    align: right;
`

const styleGray = {backgroundColor : '#AFAFAF'};
const EditPage = () => {
  <Upload> 
  <h4 className='display-4 text-center mb-4'>
    <i className='fab fa-react' /> React File Upload
  </h4> <FileUpload /> </Upload>
    //states
const [ride_name, setRide_name] = useState('');
const [rideModel, setRideModel] = useState('');
const [rideArea, setRideArea] = useState('');
const [theoryCapacity, setTheoryCapacity] = useState(0);
const [targetCapacity, setTargetCapacity] = useState(0);
const [maxVehicles, setMaxVehicles] = useState(0);
const [minVehicles, setMinVehicles] = useState(0);
const [maxSeats, setMaxSeats] = useState(0);
const [minStaff, setMinStaff] = useState(0);
const [maxStaff, setMaxStaff] = useState(0);
const [powerReq, setPowerReq] = useState('');
const [maxHeight, setMaxHeight] = useState('');
const [trackLength, setTrackLength] = useState('');

//for the drop down menu
const [rideSelect, setRideSelect] = useState('');

//state to get all attractions
const [attractionList, setAttractionList] = useState([]);
//send the attraction data to the backend running on port 3001
//specifically /addAttraction

//http request
const getAttractions = () => {
  Axios.get('http://localhost:3001/getAttraction').then( (res) => {
    console.log(res); //response
    setAttractionList(res.data);
    return attractionList;
  });
}

const editAttraction = () =>{
  Axios.put('http://localhost:3001/editAttraction', {
                ride_name: ride_name,
                rideModel: rideModel,
                rideArea: rideArea,
                theoryCapacity: theoryCapacity,
                targetCapacity: targetCapacity,
                minVehicles: minVehicles,
                maxVehicles: maxVehicles,
                maxSeats: maxSeats,
                minStaff: minStaff,
                maxStaff: maxStaff,
                powerReq: powerReq,
                maxHeight: maxHeight,
                trackLength: trackLength,

                rideSelect: setRideSelect

                }).then(() =>{
                  alert('successful insert');
              }).then( () => {
                console.log("Successfully sent to port 3001");
              });
};

return (
    <>
    {useEffect(() =>{
      getAttractions();
    })}
    <OuterBorder>
    <EditBar> Edit an Attraction </EditBar>


    <RideSelect onChange={(e) => {
                        setRideSelect(e.target.value);
                      }}>
            {attractionList.map((val, key) => {

              return (
                  <>                                
                          <option>{val.ride_name}</option>
                  </>
              );
              })}

          </RideSelect>

    <EditBorder>
      <InfoBar> Basic Information</InfoBar>


    <table className="table table-bordered table-striped">
        <thead>
          <tr style = {styleGray}>

            <th scope="col">Property</th>
            <th scope="col">Value</th>
          </tr>
        </thead>
        <tbody>

        <tr>
        <td>Name</td>
        <td>   

        <input type='text' name='ride_name' onChange={(e) => {
                    setRide_name(e.target.value);
                  }}></input> 

          </td>
        </tr>

        <tr>
        <td>Model</td>
        <th> 
            <input type='text' name='rideModel' onChange={(e) => {
                    setRideModel(e.target.value);
                  }}></input> 
        </th>
        </tr>

        <tr>
        <td>Area</td>
        <th> 
            <input type='text' name='rideArea' onChange={(e) => {
                      setRideArea(e.target.value);
                    }}></input>
        </th>
        </tr>

        <tr>
        <td>Theoretical Capacity</td>
        <th> 
            <input type='number' name='theoryCapacity' onChange={(e) => {
                          setTheoryCapacity(e.target.value);
                        }}></input>
        </th>
        </tr>

        <tr>
        <td>Target Capacity</td>
        <th> 
            <input type='number' name='targetCapcity' onChange={(e) => {
                          setTargetCapacity(e.target.value);
                        }}></input>  
        </th>
        </tr>

        <tr>
        <td>Max Vehicles</td>
        <th>
            <input type='number' name='maxVehicles' onChange={(e) => {
                          setMaxVehicles(e.target.value);
                        }}></input>
        </th>
        </tr>

        <tr>
        <td>Min Vehicles</td>
        <th>
            <input type='number' name='minVehicles' onChange={(e) => {
                          setMinVehicles(e.target.value);
                        }}></input>
        </th>
        </tr>

        <tr>
        <td>Seats per Vehicle</td>
        <th>
            <input type='number' name='maxSeats' onChange={(e) => {
                          setMaxSeats(e.target.value);
                        }}></input>
        </th>
        </tr>

        <tr>
        <td>Max Employees</td>
        <th>
            <input type='number' name='maxStaff' onChange={(e) => {
                          setMaxStaff(e.target.value);
                        }}></input>
        </th>
        </tr>

        <tr>
        <td>Min Employees</td>
        <th>
            <input type='number' name='minStaff' onChange={(e) => {
                          setMinStaff(e.target.value);
                        }}></input>
        </th>
        </tr>

        <tr>
        <td>Power Requirement</td>
        <th>
              <input type='text' name='powerReq' onChange={(e) => {
                            setPowerReq(e.target.value);
                          }}></input>
        </th>
        </tr>

        <tr>
        <td>Max Height</td>
        <th>
              <input type='text' name='maxHeight' onChange={(e) => {
                            setMaxHeight(e.target.value);
                          }}></input>
        </th>
        </tr>

        <tr>
        <td>Track Length</td>
        <th>
            <input type='text' name='trackLength' onChange={(e) => {
                          setTrackLength(e.target.value);
                        }}></input>
        </th>
        </tr>

        </tbody>

        <EditButton onClick={editAttraction}>Edit Attraction</EditButton>

        </table>

    </EditBorder>
    <ReportInfo>
        <Box> 
        Required <ul>  Documentation</ul> </Box>
        <ul>Operations Report</ul>  
        <ul>Turnstile Report</ul> 
        <ul>Lockout Report</ul>
    </ReportInfo>

    <SafetyInfo>
      <InfoBar>Safety Critical Information</InfoBar>
      <table className="table table-bordered table-striped">
        <thead>
          <tr style = {styleGray}>
            <th scope="col">Property</th>
            <th scope="col">Value</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>Weather Code</td>
            <td>Alpha</td>
          </tr>

          <tr>
            <td>Max Operating Wind</td>
            <td>34 MPH</td>
          </tr>

          <tr>
            <td>Min Operating Temperature</td>
            <td>41 F</td>
          </tr>

          <tr>
            <td>Power Redundancy</td>
            <td>On-Site</td>
          </tr>

          <tr>
            <td>Number of Gates</td>
            <td>11</td>
          </tr>
        </tbody>
      </table>
    </SafetyInfo>
    <PhoneInfo>
      <InfoBar>Phone Information</InfoBar>
      <table className="table table-bordered table-striped">
        <thead>
          <tr style = {styleGray}>
            <th scope="col">Name</th>
            <th scope="col">Number</th>
            <th scope="col">Out</th>
            <th scope="col">In</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>Primary</td>
            <td>3152</td>
            <td>
              <input type='checkbox'></input>
            </td>
            <td>
              <input type='checkbox'></input>
            </td>
          </tr>

          <tr>
            <td>Secondary</td>
            <td>3153</td>
            <td>
              <input type='checkbox'></input>
            </td>
            <td>
              <input type='checkbox'></input>
            </td>
          </tr>

          <tr>
            <td>Tertiary</td>
            <td>3154</td>
            <td>
              <input type='checkbox'></input>
            </td>
            <td>
              <input type='checkbox'></input>
            </td>
          </tr>
        </tbody>
      </table>
    </PhoneInfo>
    </OuterBorder>
    </>
    );
}

export default EditPage;
