import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import plus from './plusSign.png';
import { AttractionModal } from './AttractionModal';
import { GlobalStyle } from '../../globalStyles';
import Axios from 'axios';
import { MdClose } from 'react-icons/md';

const Border = styled.div`
overflow: hidden;
background: transparent;
margin: 15px 0px;
position: absolute;
left: 0px;
width: 25%;
height: 250px;
border-right: 2px solid black;
`

const InfoContainer = styled.div`
overflow: hidden;
overflow-y: scroll;
background: transparent;
position: absolute;
left: 0px;
width: 100%;
height: 210px;
`;

const Label1 = styled.h1`
margin-top: 2%;
margin-left: 0%;
font-size: 175%;
text-align: center;
font-weight: normal;
`

const CallsBorder = styled.div`
overflow: hidden;
background: transparent;
margin: 15px 0px;
position: absolute;
left: 25%;
width: 20%;
height: 250px;
border-right: 2px solid black;
align-content: center;
`
const RideName = styled.div`
text-align: center;
font-size: 30px;
`

const PauseButton = styled.button`
margin-top: 60px;
margin-left: 20px;
justify-content: center;
height: 17%;
width: 85%;
border: 2px solid black;
background: transparent;
`
const EndButton = styled.button`
margin-top: 2px;
margin-left: 20px;
justify-content: center;
height: 17%;
width: 85%;
border: 2px solid black;
background: transparent;
`
const AddIntervalsBorder = styled.tr`
overflow: hidden;
background: transparent;
margin: 15px 0px;
position: absolute;
left: 45%;
width: 43.8%;
height: 250px;
overflow: hidden;
overflow-x: scroll;
white-space: nowrap;
`;

const IntervalCard = styled.div`
width: 50%;
height: 100%;
border-right: 2px solid black;
display: inline-block;
`
const CardTime = styled.div`
position: absolute;
top: 0%;
height: 12%;
width: 50%;
border-bottom: 2px solid black;
text-align: center;
border-bottom: 2px solid black;
font-size: 90%;
font-weight: bold;
`
const CardCollect = styled.div`
position: absolute;
top: 12%;
text-align: left;
font-size: 90%;
font-weight: bold;
`

const CardStarting = styled.div`
border-top: 2px solid black;
position: absolute;
top: 47%;
height: 28%;
width: 50%;
border-bottom: 2px solid black;
text-align: left;
font-size: 90%;
font-weight: bold;
`
const CardEnding = styled.div`
position: absolute;
top: 75%;
height: 28%;
width: 20%;
text-align: left;
font-size: 90%;
font-weight: bold;
`
const Image = styled.img`
display:flex;
height: 70px;
border: none;
`
const Button = styled.button`
display: inline-block;
background: transparent;
position: absolute;
left: 17.5%;
top: 35%;
border: none;
`
const RideSelect = styled.select`
align-content: right;
`
const RideButton = styled.button`
background: lightgray;
font-size: 20px;
width: 30%;
border-radius: 10px;
`

const DeleteButton = styled(MdClose)`
  cursor: pointer;
  position: absolute;
  top: 0px;
  right: 15px;
  width: 20px;
  height: 30px;
  padding: 0;
`

const Variables = styled.li`
font-size: 11.5px;
margin-left: 10%;
`
const InputVariables = styled.input`
width: 70px;
`
const SubmitButton = styled.button`
  position: absolute;
  bottom: 0px;
  left: 200px;
  height: 20px;
  font-size: 10px;
`

const styleBlue = {backgroundColor : '#8EAAD1'};
const styleGray = {backgroundColor : '#AFAFAF'};
const AttractionIntervals = (props) => {
    const [showModal, setShowModal] = useState(false);

    const openModal = () => {
    setShowModal(prev => !prev);
    };

        //states
        const [intervalList, setIntervalList] = useState([]);
        const [rideSelect, setRideSelect] = useState('');

        var cardCount = 0;
        var currentRide = '';

        //state to get all attractions
        const [attractionList, setAttractionList] = useState([]);

        //recieve data from backend to display
        const GetAttractions = () => {
            //console.log(res.data)
            useEffect(() => {
                Axios.get('http://localhost:3001/getAttraction').then(res => {
                setAttractionList(res.data);
                }).catch(err => console.log(err));
                }, [])
        }

        const GetIntervals = () => {

            Axios.get(`http://localhost:3001/getInterval/:${rideSelect}`).then(res => {
            console.log(rideSelect)
            setIntervalList(res.data)
            }).catch(err => console.log(err));
        }

        const deleteInterval = (ride_name) => {
            Axios.delete(`http://localhost:3001/deleteInterval/${ride_name}`);
          };

