import React, { useState, useRef, useEffect, useCallback } from 'react';

import { useSpring, animated } from 'react-spring';
import styled from 'styled-components';
import { MdClose } from 'react-icons/md';
import Axios from 'axios'

import { NavLink as Link } from 'react-router-dom';



export const NavLink = styled(Link)`
  margin-left: 40%;
  font: bold 20px Arial;
  text-decoration: none;
  background-color: #EEEEEE;
  color: #333333;
  border-top: 1px solid #CCCCCC;
  border-right: 1px solid #333333;
  border-bottom: 1px solid #333333;
  border-left: 1px solid #CCCCCC;
`;

const Upload = styled.div`
    border: 0px solid;   
    height: 54%;
    width: 43%;
    position: absolute;
    top: 6%;
    right: 0%;
`;

const OuterBorder = styled.div`
  width: 100%;
  height: 685px;
  position: absolute;
  margin: 13px 0px;
  border: solid 2px;
`;

const DeleteBar = styled.div`
  font-family: "Lucida Console", monospace;
  width: 100%;
  height: 6%;
  padding: 6px;
  border-bottom: 2px solid;
  font-size: 22px;
`;

const EditBorder = styled.nav`
  width: 40%;
  height: 607px;
  border-right: 2px solid;
  font-family: 'Arial', sans-serif;
  font-size: 18px;
  overflow: scroll;
`;

const InfoBar = styled.div`
  font-family: "Lucida Console", monospace;
  font-size: 22px;
  align-content: center;
  border-bottom: 2px solid;
  padding-left: 10.5%;
  width: 40%;
  border-right: 2px solid;
`;

const InfoBar2 = styled.div`
  font-family: "Lucida Console", monospace;
  font-size: 22px;
  align-content: center;
  border-bottom: 2px solid;
  padding-left: 10.5%;
  width: 100%;
  border-right: 2px solid;
`;

const SafetyInfo = styled.div`
    font-family: 'Arial', sans-serif;
    font-size: 19px;
    text-align: center;
    position: absolute;
    right: 30%;
    bottom: 0%;
    height: 41%;
    width: 30%;
    border: 2px solid;
    overflow: hidden;
    overflow-y: scroll;
`;

const PhoneInfo = styled.div`
    font-family: 'Arial', sans-serif;
    font-size: 19px;
    text-align: center;
    width: 30%;
    height: 41%;
    position: absolute;
    left: 69.9%;
    bottom: 0%;
    border: 2px solid;
    overflow: hidden;
    overflow-y: scroll;
`;

const ReportInfo = styled.div`
    overflow: hidden;
    border: 2px solid;
    font-family: 'Arial', sans-serif;
    font-size: 16px;
    position: absolute;
    padding: 5px;
    text-align: center;
    width: 17%;
    height: 365px;
    right: 43%;
    top: 5.8%;
`;

const Box = styled.div`
    border-bottom: 2px solid;   
    height: 14%;
    margin-bottom: 4px;
`;

const DeleteButton = styled.button`
padding-right: 40%;
padding-left: 40%;
`

const RideSelect = styled.select`
    align: right;
`



const styleGray = {backgroundColor : '#AFAFAF'};
export default function DeletePage() {



  //states
  const [ride_name, setRide_name] = useState('');
  const [dailyOpening, setDailyOpening] = useState('');
  const [dailyClosing, setDailyClosing] = useState('');
  const [theoryCapacity, setTheoryCapacity] = useState('');
  const [targetCapacity, setTargetCapacity] = useState('');
  const [minVehicles, setMinVehicles] = useState('');
  const [maxVehicles, setMaxVehicles] = useState('');
  const [maxSeats, setMaxSeats] = useState('');
  const [minStaff, setMinStaff] = useState('');
  const [maxStaff, setMaxStaff] = useState('');
  const [parkSection, setParkSection] = useState('');
  const [weatherCode, setWeatherCode] = useState('');
  const [rideType, setRideType] = useState('');

const [rideSelect, setRideSelect] = useState('');

//state to get all attractions
const [attractionList, setAttractionList] = useState([]);
//send the attraction data to the backend running on port 3001
//specifically /addAttraction

//recieve data from backend to display
const getAttractions = () => {
  Axios.get('http://localhost:3001/getAttraction').then( (res) => {
      //console.log(res.data)
      return setAttractionList(res.data);
  });

}

const getDailyOpening = () => {
  Axios.get('http://localhost:3001/getDailyOpening').then( (res) =>{
  return res.data;

  })
}


const deleteAttraction = (ride_name) => {
  Axios.delete(`http://localhost:3001/deleteAttraction/${ride_name}`);
};

return (
    <>

    <OuterBorder>
    <DeleteBar> Delete an Attraction </DeleteBar>
    <RideSelect onChange={(e) => {
                        setRideSelect(e.target.value);
                      }}>
                        <option>Select Attraction</option>
            {attractionList.map((val, key) => {

              return (
                  <>                                
                          <option>{val.ride_name}</option>
                  </>
              );
              })}

    </RideSelect>
    <InfoBar> Basic Information</InfoBar>
    <EditBorder>



        {useEffect(() =>{
              {window.addEventListener('load', getAttractions())}
            })}
        <table className="table table-bordered table-striped">
        <thead>
          <tr style = {styleGray}>
            <th scope="col">Property</th>
            <th scope="col">Value</th>
          </tr>
        </thead>
        <tbody>


        <tr>
        <td>Name</td>
        <td>                 

          <label>{attractionList.map((val, key) => {
                var ride_name;
                if (rideSelect == val.ride_name){
                    ride_name = val.ride_name;
                }
              return (
                  <> 
                    {ride_name}   
                  </>
              );
              })}
            </label>

        </td>
        </tr>

        <tr>
        <td>Daily Opening</td>
        <th> 

          <label>{attractionList.map((val, key) => {
              var dailyOpening;
              if (rideSelect == val.ride_name){
                  dailyOpening = val.dailyOpening;
              }
            return (
                <> 
                  {dailyOpening}   
                </>
            );
            })}
          </label>

        </th>
        </tr>

        <tr>
        <td>Daily Closing</td>
        <th> 

            <label>{attractionList.map((val, key) => {
              var dailyClosing;
              if (rideSelect == val.ride_name){
                  dailyClosing = val.dailyClosing;
              }
            return (
                <> 
                  {dailyClosing}   
                </>
            );
            })}
          </label>

        </th>
        </tr>

        <tr>
        <td>Theoretical Capacity</td>
        <th> 

            <label>{attractionList.map((val, key) => {
              var theoryCapacity;
              if (rideSelect == val.ride_name){
                  theoryCapacity = val.theoryCapacity;
              }
            return (
                <> 
                  {theoryCapacity}   
                </>
            );
            })}
          </label>

        </th>
        </tr>

        <tr>
        <td>Target Capacity</td>
        <th> 

            <label>{attractionList.map((val, key) => {
              var targetCapacity;
              if (rideSelect == val.ride_name){
                  targetCapacity = val.targetCapacity;
              }
            return (
                <> 
                  {targetCapacity}   
                </>
            );
            })}
          </label>

        </th>
        </tr>

        <tr>
        <td>Maximum Vehicles</td>
        <th>

            <label>{attractionList.map((val, key) => {
              var maxVehicles;
              if (rideSelect == val.ride_name){
                  maxVehicles = val.maxVehicles;
              }
            return (
                <> 
                  {maxVehicles}   
                </>
            );
            })}
          </label>

        </th>
        </tr>

        <tr>
        <td>Minimum Vehicles</td>
        <th>

            <label>{attractionList.map((val, key) => {
              var minVehicles;
              if (rideSelect == val.ride_name){
                  minVehicles = val.minVehicles;
              }
            return (
                <> 
                  {minVehicles}   
                </>
            );
            })}
          </label>

        </th>
        </tr>

        <tr>
        <td>Seats per Vehicle</td>
        <th>

           <label>{attractionList.map((val, key) => {
              var dailyClosing;
              if (rideSelect == val.ride_name){
                  dailyClosing = val.dailyClosing;
              }
            return (
                <> 
                  {dailyClosing}   
                </>
            );
            })}
          </label>

        </th>
        </tr>

        <tr>
        <td>Maximum Employees</td>
        <th>

        <label>{attractionList.map((val, key) => {
              var maxStaff;
              if (rideSelect == val.ride_name){
                  maxStaff = val.maxStaff;
              }
            return (
                <> 
                  {maxStaff}   
                </>
            );
            })}
          </label>

        </th>
        </tr>

        <tr>
        <td>Minimum Employees</td>
        <th>

        <label>{attractionList.map((val, key) => {
              var minStaff;
              if (rideSelect == val.ride_name){
                  minStaff = val.minStaff;
              }
            return (
                <> 
                  {minStaff}   
                </>
            );
            })}
          </label>

        </th>
        </tr>

        <tr>
        <td>Park Section</td>
        <th>

        <label>{attractionList.map((val, key) => {
              var parkSection;
              if (rideSelect == val.ride_name){
                  parkSection = val.parkSection;
              }
            return (
                <> 
                  {parkSection}   
                </>
            );
            })}
          </label>

        </th>
        </tr>

        <tr>
        <td>Weather Code</td>
        <th>

        <label>{attractionList.map((val, key) => {
              var weatherCode;
              if (rideSelect == val.ride_name){
                  weatherCode = val.weatherCode;
              }
            return (
                <> 
                  {weatherCode}   
                </>
            );
            })}
          </label>

        </th>
        </tr>

        <tr>
        <td>Type</td>
        <th>

        <label>{attractionList.map((val, key) => {
              var rideType;
              if (rideSelect == val.ride_name){
                  rideType = val.rideType;
              }
            return (
                <> 
                  {rideType}   
                </>
            );
            })}
          </label>

        </th>
        </tr>

        </tbody>

          <DeleteButton onClick={() => {
            deleteAttraction(rideSelect) //ride selected in drop down menu
            window.alert(`The ride: ${ride_name} has been deleted`);
            window.location.href='/newAttraction';
          }}>Delete Attraction</DeleteButton>

        </table>



    </EditBorder>
    <ReportInfo>
        <Box> 
        Required <ul>  Documentation</ul> </Box>
        <ul>Operations Report</ul>  
        <ul>Turnstile Report</ul> 
        <ul>Lockout Report</ul>
    </ReportInfo>

    <Upload>  
    <h4 className='display-4 text-center mb-4'>
      <i className='fab fa-react' /> Titan Upload</h4> </Upload>

    <SafetyInfo>
      <InfoBar2>Safety Critical Information</InfoBar2>
      <table className="table table-bordered table-striped">
        <thead>
          <tr style = {styleGray}>
            <th scope="col">Property</th>
            <th scope="col">Value</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>Weather Code</td>
            <td>Alpha</td>
          </tr>

          <tr>
            <td>Max Operating Wind</td>
            <td>34 MPH</td>
          </tr>

          <tr>
            <td>Min Operating Temperature</td>
            <td>41 F</td>
          </tr>

          <tr>
            <td>Power Redundancy</td>
            <td>On-Site</td>
          </tr>

          <tr>
            <td>Number of Gates</td>
            <td>11</td>
          </tr>
        </tbody>
      </table>
    </SafetyInfo>
    <PhoneInfo>
      <InfoBar2>Phone Information</InfoBar2>
      <table className="table table-bordered table-striped">
        <thead>
          <tr style = {styleGray}>
            <th scope="col">Name</th>
            <th scope="col">Number</th>
            <th scope="col">Out</th>
            <th scope="col">In</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>Primary</td>
            <td>3152</td>
            <td>
              <input type='checkbox'></input>
            </td>
            <td>
              <input type='checkbox'></input>
            </td>
          </tr>

          <tr>
            <td>Secondary</td>
            <td>3153</td>
            <td>
              <input type='checkbox'></input>
            </td>
            <td>
              <input type='checkbox'></input>
            </td>
          </tr>

          <tr>
            <td>Tertiary</td>
            <td>3154</td>
            <td>
              <input type='checkbox'></input>
            </td>
            <td>
              <input type='checkbox'></input>
            </td>
          </tr>
        </tbody>
      </table>
