import React, { useState, useRef, useEffect, useCallback } from 'react';
import { useSpring, animated } from 'react-spring';
import styled from 'styled-components';
import { MdClose } from 'react-icons/md';
import Axios from 'axios';


const Background = styled.div`
  width: 0px;
  height: 0px;
  position: fixed;
  display: flex;
  align-items: center;
  z-index: 10;
`;

const ModalWrapper = styled.div`
  top: 200px;
  width: 485px;
  height: 100%;
  box-shadow: 0 5px 16px rgba(0, 0, 0, 0.2);
  background: grey;
  position: relative;
  border-radius: 10px;
`;


const ModalContent = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: left;
  align-items: left;
  line-height: 1.8;
  p {
    margin-bottom: 1rem;
  }
  form {
    text-align: left;
    color: lightblue;
  }
  header {

    font-size: 20pt;
    text-align: left;
  }
`;

const CloseModalButton = styled(MdClose)`
  cursor: pointer;
  position: absolute;
  top: 0px;
  right: 20px;
  width: 20px;
  height: 40px;
  padding: 0;
  z-index: 10;
`;

const InputStyle = styled.input`
margin-bottom: 20px;
width: 40%;
`
const Submit = styled.button`
margin-top: 20px;
width: 50%;
position: relative;
left: 20%;
`
const TR = styled.div`
padding-right: 10px;
font-size: 17px;
`

export const Menu = styled.div`
  display: flex;
  width: 100%;
  overflow-x: auto;
`;

export const AttractionModal = ({ showModal, setShowModal }) => {
  const modalRef = useRef();

  const animation = useSpring({
    config: {
      duration: 250
    },
    opacity: showModal ? 1 : 0,
    transform: showModal ? `translateY(0%)` : `translateY(100%)`
  });

  const closeModal = e => {
    if (modalRef.current === e.target) {
      setShowModal(false);
    }
  };

  const keyPress = useCallback(
    e => {
      if (e.key === 'Escape' && showModal) {
        setShowModal(false);
        console.log('I pressed');
      }
    },
    [setShowModal, showModal]
  );

  useEffect(() => {
      document.addEventListener('keydown', keyPress);
      return () => document.removeEventListener('keydown', keyPress);
    },
    [keyPress]
  );

  //states
  const [startingTime, setStartingTime] = useState('');
  const [endingTime, setEndingTime] = useState('');
  const [timeValue, setTime] = useState('');
  const [typeState, setTypeState] = useState([]);
  const [unitState, setUnitState] = useState("");
  const [intervalList, setIntervalList] = useState([]);
  useEffect(() => {
    let typeState = [
      { id: 1, type: "Wait Time"},
      { id: 2, type: "Throughput"},
      { id: 3, type: "Available Seats"},
      { id: 4, type: "Available Down"},

    ];
    setTypeState(
      typeState.map(d => {
        return {
          select: false,
          id: d.id,
          type: d.type,
        };
      })
    );
  }, []);
  //state to get all attractions
  //send the attraction data to the backend running on port 3001
  //specifically /addAttraction

  const submitInterval = () =>{
    Axios.post('http://localhost:3001/addInterval', {
                  timeValue: timeValue,
                  startingTime: startingTime,
                  endingTime: endingTime}).then(() =>{
                    alert('successful insert');
                }).then( () => {
                  console.log("Successfully sent to port 3001");
                });
  };

  return (
    <>
      {showModal ? (
        <Background onClick={closeModal} ref={modalRef}>
          <animated.div style={animation}>
            <ModalWrapper showModal={showModal}>
              <ModalContent>

                <header>Add an Interval</header>
                <form>Time Value:</form>
                <InputStyle type='number' name='Time' onChange={(e) => {
                  setTime(e.target.value);
                }}></InputStyle>
                <form >Collect:            </form>
                  <Menu>
                {typeState.map((d, i) => ( 
                <TR key={d.id}>
             <th>

             <div class="form-check form-check-inline">
                                    <input class="form-check-input" type="checkbox" name="hobbies" id="inlineCheckboxh1" value="1" onChange={this.handleInputChange} />
                                    <label class="form-check-label" for="inlineCheckboxh1">Reading</label>
                                </div>
                                <div class="form-check form-check-inline">
                                    <input class="form-check-input" type="checkbox" name="hobbies" id="inlineCheckboxh2" value="2" onChange={this.handleInputChange} />
                                    <label class="form-check-label" for="inlineCheckboxh2">Developing</label>
                                </div>
                                <div class="form-check form-check-inline">
                                    <input class="form-check-input" type="checkbox" name="hobbies" id="inlineCheckboxh3" value="3" onChange={this.handleInputChange} />
                                    <label class="form-check-label" for="inlineCheckboxh3">Desiging</label>
                                </div>
                </th>
              <td>{d.type}</td>
            </TR>
                ))}
                </Menu>

                <form>Starting:</form>
                <InputStyle type='time' name='startingTime' onChange={(e) => {
                  setStartingTime(e.target.value);
                }}></InputStyle>

                <form>Ending:</form>
                <InputStyle type='time' name='endingTime' onChange={(e) => {
                  setEndingTime(e.target.value);
                }}></InputStyle>

                <Submit 
                onClick={() => { setShowModal(prev => !prev);
                                  submitInterval();
                                  setTimeout(function(){
                                    window.location.reload(); 
                                }, 1);
                               }}>Submit</Submit>




              </ModalContent>
              <CloseModalButton
                aria-label='Close modal'
                onClick={() => setShowModal(prev => !prev)}
              />

            </ModalWrapper>
          </animated.div>
        </Background>
      ) : null}
    </>
  );
};
