background-color: darkgray;
text-align: center;
padding: 3px;
font-size: 150%;
font-weight: bold;
margin: 0px 0px;
`;

export const QuickToolsButtons = styled.button`
width: 100%;
height: 21%;
padding: 1px;
font-size: 16px;
border-bottom: 1px solid black;
`;

const QuickToolsMain = () => {
const [showModal, setShowModal] = useState(false);

